mapper.insert:
INSERT INTO MAP_DATA
(ID, NAME, PX, PY, ADDRESS, MCID, MCID_NAME, AVAILABLE)
VALUES(
       #{bookmarkId}
    ,   #{name}
    ,   #{px}
    ,   #{py}
    ,   #{address}
    ,   #{mcid}
    ,   #{mcidName}
    ,   #{available}
)

mapper.select:
SELECT  COUNT(*)
FROM    MAP_DATA
WHERE   ID = #{bookmarkId}

SELECT  *
FROM    MAP_DATA
WHERE   PX BETWEEN #{minLng} AND #{maxLng}
    AND PY BETWEEN #{minLat} AND #{maxLat}
