mapper.insert:
INSERT INTO MAP_DATA
(ID, NAME, PX, PY, ADDRESS, MCID, MCID_NAME, AVAILABLE)
VALUES(
       #{bookmarkId}
    ,   #{name}
    ,   #{px}
    ,   #{py}
    ,   #{address}
    ,   #{mcid}
    ,   #{mcidName}
    ,   #{available}
)

mapper.select:
select  *
from    map_data


        id = #{id}


        and name = #{name}

SELECT  COUNT(*)
FROM    MAP_DATA
WHERE   ID = #{bookmarkId}

select  a.*
from    map_data a left join vanish_map_data b on a.id = b.id
where   (b.vanish_yn is null or b.vanish_yn = 'n')
    and cast(px as double precision) between cast(#{minLng} as double precision) and cast(#{maxLng} as double precision)
    and cast(py as double precision) between cast(#{minLat} as double precision) and cast(#{maxLat} as double precision)

        and mcid = #{mcid}


        and addr_1 = #{addr1}


        and addr_2 = #{addr2}



            name like concat('%', trim(#{item}), '%')

SELECT  *
FROM    MAP_DATA


        ADDR_1 = #{addr1}


        AND ADDR_2 = #{addr2}

select  count(*)
from    visit_log
where   IP = #{ip}
    and session_id = #{sessionId}
    and visit_dt = to_char(now(), 'YYYY-MM-DD')

mapper.insert:
insert into visit_log
(ip, visit_dt, visit_count, user_agent, session_id)
values(
        #{ip}
    ,   to_char(now(), 'YYYY-MM-DD')
    ,   1
    ,   #{userAgent}
    ,   #{sessionId}
)

mapper.update:
UPDATE VISIT_LOG
SET     VISIT_COUNT = VISIT_COUNT + 1
WHERE   IP = #{ip}
    AND VISIT_DT = TO_CHAR(NOW(), 'YYYY-MM-DD')
    AND user_agent = #{userAgent}

mapper.select:
SELECT  COUNT(*)
FROM    VANISH_MAP_DATA
WHERE   ID = #{id}

mapper.update:
update  vanish_map_data
set     request_cnt = request_cnt + 1
where   id = #{id}

update  map_data
set     py = #{py}
    ,   px = #{px}
    ,   addr_1 = #{addr1}
    ,   addr_2 = #{addr2}
    ,   addr_3 = #{addr3}
    ,   updated_at = now()
where   id = #{id}
